import React, { useEffect, useState } from 'react'
import { ChatHeader } from './ChatHeader'
import { ChatFooter } from './ChatFooter'
import { Messages } from './Messages'
import { Box } from '@mui/material'
import styled from '@emotion/styled'
import { useContext } from 'react'
import { AccountContext } from '../../account/context/AccountProvider'
import { getConversation } from '../../../service/api'

const Component = styled(Box)`
background-image: url(https://tse1.mm.bing.net/th?id=OIP.fua54HEGSIcLmcXX_gTj-gAAAA&pid=Api&P=0&h=180);
height:100vh;
`

export const ChatBox = ({open,setOpen}) => {


    const {person,account} = useContext(AccountContext);

    const[conversation,setConversation] = useState({});

    const [key, setKey] = React.useState(0);
    const handleForceUpdate = () => {
      setKey(prevKey => prevKey + 1); // Increment key to force re-render
    };

    useEffect(()=>{

        const getConversationDetails = async()=>{
            let data = await getConversation({senderId:account.sub,receiverId:person.sub})
            setConversation(data);
        }
        getConversationDetails();
        
    },[person.sub])

  return (
    <Component>
        <ChatHeader person={person} open={open} setOpen={setOpen} key={key}/>
        <Messages person={person} conversation={conversation} update={handleForceUpdate}/>
    </Component>
  )
}
