import React from 'react'
import {Dialog, Box, Typography, List, ListItem, styled } from "@mui/material"

export const Login = () => {
    const Component = styled(Box)`
    display: flex;`

    const qr = 'https://tse1.mm.bing.net/th?id=OIP.NDKNbQ-I9ApLLVp-E6HSPwHaHa&pid=Api&P=0&h=180';

    const QRcode = styled('img')({
        height: 250,
        width: 250,
        margin:'50px 0 0  50px'
    })

    const Title = styled(Typography)`
    font-size: 26px;
    font-weight: 300;
    font-style:inherit;
    `
    const StyledList = styled(List)`
    & > li {

    }
    `
    const dialogStyle = {
        height: '95%',
        marginTop: '12%',
        width: '60%',
        maxWidth: '100%',
        maxHeight: '100%',
        boxShadow: 'none',
        overflow: 'none'
    }
  return (
    <Dialog
        open ={true}
        PaperProps={{sx: dialogStyle}}
    >
        <Component>
            <Box>
                <Title>To use on your computer:</Title>
                <List>
                    <ListItem>1. Open your mobile</ListItem>
                    <ListItem>2. Scan the QR Code</ListItem>
                    <ListItem>3. Start Chatting</ListItem>
                </List>
            </Box>
            <QRcode src={qr} alt='qr code'/>
            <Box>
            </Box>
        </Component>
        </Dialog>
  )
}
