import styled from '@emotion/styled';
import { Box } from '@mui/material'
import React, { useEffect, useRef, useState } from 'react'
import { useContext } from 'react';
import { AccountContext } from '../../account/context/AccountProvider';
import { ChatFooter } from './ChatFooter';
import { getMessages, newMessage } from '../../../service/api';
import { Message } from './message';


const Wrapper = styled(Box)`
display:flex;
flex-direction:column;
height: calc(100vh - 66px);
// height:80vh;
`

const Component = styled(Box)`
  overflow-y:scroll;
  background-color:#6f6f6f;
`

const SearchWrapper = styled(Box)`
// align-self:flex-end;
margin-top:auto;
padding-bottom:15px;
`

const Container = styled(Box)`
padding: 1px 80px;
`
const MessageContainer = styled(Box)`
padding: 0 5px 0;
`

export const Messages = ({person,conversation}) => {

  const {account} = useContext(AccountContext);
  const [messages,setMessages] = useState([]);
  const [value,setValue] = useState('');
  const [file,setFile] = useState([]);

  const scrollRef = useRef();


  const sendText = async(e) => {
    const code = e.keycode||e.which;

    if(code===13 && value) {
      let  message = {
        senderId: account.sub,
        receiverId: person.sub,
        conversationId: conversation._id,
        type:'text',
        text: value
      }
      await newMessage(message);
      setValue('');
    }
}

  useEffect(()=>{
    const getMessageDetails = async () => {
      let data = await getMessages(conversation._id);
      console.log(data);
      setMessages(data);
    }
    if(conversation){
      getMessageDetails();
    }
  },[conversation._id,value])

  useEffect(()=>{
    scrollRef.current?.scrollIntoView({ transition:'smooth' })
  },[messages])

  return (
    <>
     <Wrapper>
      <Component className='MessageContainer'>
        {
          messages && messages.map((message) => (
            <MessageContainer  ref={scrollRef}>
              <Message message={message}/>
            </MessageContainer>
          ))
        }
      </Component>
      <SearchWrapper>
        <ChatFooter setValue={setValue} sendText={sendText} value={value} file={file} setFile={setFile} />
      </SearchWrapper>
     </Wrapper>
    </>
  )
}
