import { Box, Dialog, Drawer } from '@mui/material'
import React, { useState } from 'react'
import { Menu } from './menu/Menu'
import { EmptyChat } from './chat/EmptyChat'
import styled from '@emotion/styled'
import { ChatBox } from './chat/ChatBox'
import { useContext } from 'react'
import { AccountContext } from '../account/context/AccountProvider'

const dialogStyle = {
        height: '95%',
        width: '100%',
        margin: '20px',
        borderRadius: '0',
        // minwidth: '600px',
        maxWidth: '1500px',
        maxHeight: '100%',
        overflow: 'hidden'
    }

    const Component = styled(Box)`
    display:flex`

    const LeftComponent = styled(Box)`
    min-width: 320px;
    width: 38%;
    `

    const RightComponent = styled(Box)`
    width: 75%;
    min-width: 300px;
    overflow:hidden;
    height:100%;
    border-left: 1px solid black;
    `

export const ChatDialog = () => {

    const {person} = useContext(AccountContext);
    const [openChatsDrawer, setOpenChatsDrawer] = useState(true);

  return (
    <Dialog
        open={true}
        PaperProps={{sx: dialogStyle}}
        hideBackdrop={true}
        maxWidth={'md'}
    >
        <Component>
            <LeftComponent>
                <Drawer 
                    open={openChatsDrawer}
                    // sx={{
                    // width: drawerWidth,
                    // flexShrink: 0,
                    // '& .MuiDrawer-paper': {
                    //     width: drawerWidth,
                    //     boxSizing: 'border-box',
                    // },
                    // }}
                    variant="persistent"
                    anchor="left"
                    onClose={()=>{setOpenChatsDrawer(false)}}
                    style={{
                        zIndex:1500
                    }}
                    >
                    <Menu/>
                </Drawer>
            </LeftComponent>
            <RightComponent>
                {Object.keys(person).length ? (
                    <ChatBox open={openChatsDrawer} setOpen={setOpenChatsDrawer}/>
                    ) : ( 
                    <EmptyChat/>
                )}
            </RightComponent>
        </Component>

    </Dialog>
  )
}
