import { Box, Dialog } from '@mui/material'
import React from 'react'
import { Menu } from './menu/Menu'
import { EmptyChat } from './chat/EmptyChat'
import styled from '@emotion/styled'

const dialogStyle = {
        height: '95%',
        width: '100%',
        margin: '20px',
        borderRadius: '0',
        // minwidth: '600px',
        maxWidth: '100%',
        maxHeight: '100%',
        overflow: 'hidden'
    }

    const Component = styled(Box)`
    display:flex`

    const LeftComponent = styled(Box)`
    min-width: 320px;
    width: 35%;
    `

    const RightComponent = styled(Box)`
    width: 75%;
    min-width: 300px;
    overflow:hidden;
    height:100%;
    border-left: 1px solid black;
    `

export const ChatDialog = () => {
  return (
    <Dialog
        open={true}
        PaperProps={{sx: dialogStyle}}
        hideBackdrop={true}
        maxWidth={'md'}
    >
        <Component>
            <LeftComponent>
                <Menu/>
            </LeftComponent>
            <RightComponent>
                <EmptyChat/>
            </RightComponent>
        </Component>

    </Dialog>
  )
}
