import { Box, Dialog } from '@mui/material'
import React from 'react'
import { Menu } from './menu/Menu'
import { EmptyChat } from './chat/EmptyChat'

const dialogStyle = {
        height: '95%',
        width: '100%',
        margin: '20px',
        borderRadius: '0',
        // minwidth: '600px',
        maxWidth: '100%',
        maxHeight: '100%',
        overflow: 'hidden'
    }


export const ChatDialog = () => {
  return (
    <Dialog
        open={true}
        PaperProps={{sx: dialogStyle}}
        hideBackdrop={true}
    >
        <Box>
            <Box>
                <Menu/>
            </Box>
            <Box>
                <EmptyChat/>
            </Box>
        </Box>

    </Dialog>
  )
}
